import React, {Fragment} from 'react';
import {Link} from "react-router-dom";

const Pirate = ({pirate}) => {

  if (!pirate){
    return "Loading..."
  }

  const editUrl = "/pirates/" + pirate.id
  return (
    <div>
    <p>
    {pirate.firstName} {pirate.lastName}
    </p>
    <p>Age: {pirate.age}</p>
    <p>Ship: {pirate.ship.name}</p>
    <Link to={editUrl}><button type="button">View Details</button></Link>
    </div>
  )
}

export default Pirate;
